package TestPackage;

public class DataTypeProgram {
	
        public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Boolean value example 
		
      Boolean  b= true;
     System.out.println("value of Boolean variable :" + b);
     
     //character value example
     char char_variable = 'A';
     System.out.println("character value :"  +char_variable);
     
     //Byte variable example 
    
     Byte byte_variable =100;
     System.out.println("Value of byte :" + byte_variable );
     
     //short variable example 
     
     short short_variable =201;
     System.out.println("value of short :"+ short_variable);
     
     //integer variable example
     
     int int_variable = 10000;
     System.out.println("value of integer :" + int_variable);
     
     //long variable example
     
     long long_variable = 35000;
     System.out.println("value of long :"+ long_variable);
     
     //float variable example
     
     float float_variable = 31.86f;
     System.out.println("value of float: " + float_variable );
     
     //double variable example
     
     double double_variable =31.86245697d;
     System.out.println("value of double: " + double_variable);
     
     //string variable example
     
     String dog="Golden retriever", car="BMW", mobile="iphone 13";
     System.out.println("(dog:" + dog + "),car: "+ car + ", mobile:" +mobile);
     
    
     
     
     

     
	}

}
